import { FaTimes } from 'react-icons/fa'

const Task = (props) => {
  return (
    <div className="task">
        <h3>{props.task.name} <FaTimes onClick={props.onDelete} style={{ color:"red", cursor:"pointer"}}/></h3>
        <p>{props.task.day}</p>
    </div>
  )
}

export default Task
